Widget foodItem(Product food,
    {double imgWidth, onLike, onTapped, bool isProductPage = false}) {
  return Container(
    width: 150,
    height: 150,
    color: Colors.white,
    margin: EdgeInsets.only(left: 10),
    child: Stack(
      children: <Widget>[
        Container(
            width: 150,
            height: 125,
            child: RaisedButton(
                color: Colors.white60,
                elevation: (isProductPage) ? 20 : 3,
                shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(5)),
                onPressed: onTapped,
                child: Hero(
                    transitionOnUserGestures: true,
                    tag: food.id,
                    child: Image.asset(food.image,
                        width: (imgWidth != null) ? imgWidth : 150)))),
        Positioned(
          bottom: (isProductPage) ? 10 : 140, // fav konumu
          right: -20,
          child: FlatButton(
            //padding: EdgeInsets.all(10),
            shape: CircleBorder(),
            onPressed: onLike,
            child: Icon(
              (food.userLiked) ? Icons.favorite : Icons.favorite_border,
              color: (food.userLiked) ? primaryColor : darkText,
              size: 30, //fav boyut
            ),
          ),
        ),
        Positioned(
          //ürün ile ilgili bilgiler
          bottom: 5, //ürün açıklamasının konumu
          left: 2,
          child: (!isProductPage)
              ? Column(
                  mainAxisAlignment: MainAxisAlignment.start,
                  crossAxisAlignment: CrossAxisAlignment.start,
                  children: <Widget>[
                    Text(food.name, style: foodNameText),
                    Text(food.price.toString(), style: priceText),
                  ],
                )
              : Text(' '),
        ),
        /*Positioned( //% kaç indirim var kısmı
            top: 0,
            left: 0,
            child: (food.discount != null)
                ? Container(
              padding:
              EdgeInsets.only(top: 2, left: 2, right: 2, bottom: 2),
              decoration: BoxDecoration(
                  color: Colors.grey[600],
                  borderRadius: BorderRadius.circular(50)),
              child: Text('-' + food.discount.toString() + '%',
                  style: TextStyle(
                      color: Colors.white, fontWeight: FontWeight.w700)),
            )
                : SizedBox(width: 0))*/
      ],
    ),
  );
}

