Widget foodItemForCart(Product food,
    {double imgWidth, onDelete, onTapped, bool isProductPage = false}) {
  return Container(
    width: 350,
    height: 150,
    color: Colors.white,
    margin: EdgeInsets.only(),
    child: Stack(
      children: <Widget>[
        Container(
            width: 300,
            height: 140,
            child: RaisedButton(
                color: Colors.white60,
                elevation: (isProductPage) ? 20 : 8,
                shape: RoundedRectangleBorder(
                    borderRadius: BorderRadius.circular(5)),
                onPressed: onTapped,
                child: Hero(
                    transitionOnUserGestures: true,
                    tag: food.id,
                    child: Image.asset("images/3.jpg",
                        width: (imgWidth != null) ? imgWidth : 200)))),
        Positioned(
          bottom: (isProductPage) ? 10 : 10, // fav konumu
          right: -20,
          child: FlatButton(//sil butonu
            padding: EdgeInsets.all(5),
            shape: CircleBorder(),
            onPressed: onDelete,
            child: Icon(
              (food.userLiked) ? Icons.delete_forever : Icons.delete,//user liked ise ilk icon değilse ikinci icon
              color: (food.userLiked) ? primaryColor : darkText,
              size: 40, //fav boyut
            ),
          ),
        ),
        Positioned(
          //ürün ile ilgili bilgiler
          bottom: 5, //ürün açıklamasının konumu
          left: 2,
          child: (!isProductPage)
              ? Column(
            mainAxisAlignment: MainAxisAlignment.start,
            crossAxisAlignment: CrossAxisAlignment.start,
            children: <Widget>[
              Text(food.name, style: foodNameText),
              Text(food.price.toString(), style: priceText),
            ],
          )
              : Text(' '),
        ),
      ],
    ),
  );
}

